import React, { useState, useEffect } from 'react';
import axios from 'axios';

function LoginLogs({ onClose }) {
  const [logs, setLogs] = useState([]);

  useEffect(() => {
    const fetchLogs = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/logs');
        console.log('Logs recibidos:', response.data);
        if (response.data && Array.isArray(response.data)) {
          setLogs(response.data);
        }
      } catch (error) {
        console.error('Error al obtener logs:', error);
      }
    };
    fetchLogs();
  }, []);

  const formatDate = (dateString) => {
    try {
      return new Date(dateString).toLocaleString('es-ES');
    } catch (error) {
      return dateString;
    }
  };

  return (
    <div className="logs-container">
      <h2>Historial de Inicios de Sesión</h2>
      <table>
        <thead>
          <tr>
            <th>Usuario</th>
            <th>ID</th>
            <th>Fecha y Hora</th>
            <th>Estado</th>
          </tr>
        </thead>
        <tbody>
          {logs.length > 0 ? (
            logs.map((log, index) => (
              <tr key={index}>
                <td>{log.user_name}</td>
                <td>{log.user_id}</td>
                <td>{formatDate(log.login_time)}</td>
                <td>{log.status === 'success' ? 'Exitoso' : 'Fallido'}</td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="4" style={{textAlign: 'center'}}>
                No hay registros de inicio de sesión
              </td>
            </tr>
          )}
        </tbody>
      </table>
      <button onClick={onClose} className="close-button">Cerrar</button>
    </div>
  );
}

export default LoginLogs;
