import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import WebcamCapture from './WebcamCapture';
import UserList from './UserList';

function Login() {
  const navigate = useNavigate();
  const [identification, setIdentification] = useState('');
  const [password, setPassword] = useState('');
  const [photo, setPhoto] = useState(null);
  const [message, setMessage] = useState('');
  const [showUserList, setShowUserList] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/login', {
        identification,
        password,
        photo
      });

      if (response.data.success) {
        // Guardar el nombre del usuario
        localStorage.setItem('userName', response.data.name);
        setMessage('Login exitoso');
        // Redirigir a Home después de un login exitoso
        setTimeout(() => navigate('/home'), 1000);
      }
    } catch (error) {
      setMessage(error.response?.data?.error || 'Error en el login');
    }
  };

  return (
    <div className="login-container">
      <h2>Iniciar Sesión</h2>
      <button 
        onClick={() => setShowUserList(true)} 
        className="view-users-button"
      >
        Ver Usuarios Registrados
      </button>
      {showUserList && <UserList onClose={() => setShowUserList(false)} />}
      {message && <p className="message">{message}</p>}
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <input
            type="text"
            value={identification}
            onChange={(e) => setIdentification(e.target.value)}
            placeholder="Número de Identificación"
            required
          />
        </div>
        <div className="form-group">
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Contraseña"
            required
          />
        </div>
        <div className="webcam-container">
          <WebcamCapture onCapture={setPhoto} />
        </div>
        <button type="submit">Iniciar Sesión</button>
      </form>
      <div className="register-link">
        <p>¿No tienes una cuenta? <Link to="/register">Regístrate aquí</Link></p>
      </div>
    </div>
  );
}

export default Login;
