import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function UserList({ onClose }) {
  const [users, setUsers] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/users');
        setUsers(response.data);
      } catch (error) {
        console.error('Error fetching users:', error);
      }
    };

    fetchUsers();
  }, []);

  const handleDelete = async (identification) => {
    try {
      await axios.delete(`http://localhost:5000/api/users/${identification}`);
      setUsers(users.filter(user => user.identification !== identification));
    } catch (error) {
      console.error('Error deleting user:', error);
    }
  };

  const handleUpdate = (user) => {
    navigate('/update-user', { state: { user } });
  };

  const formatDateTime = (dateString) => {
    if (!dateString) return 'No disponible';
    return new Date(dateString).toLocaleString();
  };

  return (
    <div className="user-list-modal">
      <div className="user-list-content">
        <h2>Usuarios Registrados</h2>
        <table>
          <thead>
            <tr>
              <th>Nombre</th>
              <th>Identificaci√≥n</th>
              <th>Fecha y Hora de Registro</th>
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {users.map(user => (
              <tr key={user._id}>
                <td>{user.name}</td>
                <td>{user.identification}</td>
                <td>{formatDateTime(user.created_at)}</td>
                <td>
                  <div className="action-buttons">
                    <button 
                      onClick={() => handleUpdate(user)}
                      className="update-button"
                    >
                      ‚úèÔ∏è
                    </button>
                    <button 
                      onClick={() => handleDelete(user.identification)}
                      className="delete-button"
                    >
                      üóëÔ∏è
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <button onClick={onClose} className="close-button">Cerrar</button>
      </div>
    </div>
  );
}

export default UserList;
