import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';
import WebcamCapture from './WebcamCapture';

function UpdateUser() {
  const location = useLocation();
  const navigate = useNavigate();
  const user = location.state?.user;

  const [formData, setFormData] = useState({
    name: user?.name || '',
    identification: user?.identification || '',
    password: '',
    photo: user?.photo || ''
  });
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handlePhotoCapture = (photo) => {
    setFormData({
      ...formData,
      photo: photo
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.put(`http://localhost:5000/api/users/${user.identification}`, formData);
      if (response.data.success) {
        setMessage('Usuario actualizado correctamente');
        setTimeout(() => navigate('/'), 2000);
      }
    } catch (error) {
      setMessage(error.response?.data?.error || 'Error actualizando usuario');
    }
  };

  return (
    <div className="container">
      <div className="update-container">
        <h2>Actualizar Usuario</h2>
        {message && <div className="message">{message}</div>}
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Nombre:</label>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
            />
          </div>
          <div className="form-group">
            <label>Identificación:</label>
            <input
              type="text"
              name="identification"
              value={formData.identification}
              disabled
            />
          </div>
          <div className="form-group">
            <label>Nueva Contraseña (dejar en blanco para mantener la actual):</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
            />
          </div>
          <div className="form-group">
            <label>Actualizar Foto:</label>
            <WebcamCapture onCapture={handlePhotoCapture} />
          </div>
          <div className="button-group">
            <button type="submit">Guardar Cambios</button>
            <button type="button" onClick={() => navigate('/')} className="back-button">
              Cancelar
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default UpdateUser;
